INSERT INTO `wp_options` VALUES("535", "_transient_dash_88ae138922fe95674369b1cb3d215a2b", "<div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'https://wordpress.org/news/2016/09/wordpress-4-6-1-security-and-maintenance-release/\'>WordPress 4.6.1 Security and Maintenance Release</a> <span class=\"rss-date\">September 7, 2016</span><div class=\"rssSummary\">WordPress 4.6.1 is now available. This is a security release for all previous versions and we strongly encourage you to update your sites immediately. WordPress versions 4.6 and earlier are affected by two security issues: a cross-site scripting vulnerability via image filename, reported by SumOfPwn researcher Cengiz Han Sahin; and a path traversal vulnerability in [&hellip;]</div></li></ul></div><div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'https://wptavern.com/wordpress-com-adds-customization-for-amp-pages-pushes-update-to-amp-plugin\'>WPTavern: WordPress.com Adds Customization for AMP Pages, Pushes Update to AMP Plugin</a></li><li><a class=\'rsswidget\' href=\'https://wptavern.com/the-deadline-to-apply-for-the-kim-parsell-scholarship-is-october-16th\'>WPTavern: The Deadline to Apply for the Kim Parsell Scholarship Is October 16th</a></li><li><a class=\'rsswidget\' href=\'https://buddypress.org/2016/10/buddypress-2-7-0-release-candidate-1/\'>BuddyPress: BuddyPress 2.7.0 Release Candidate 1</a></li></ul></div><div class=\"rss-widget\"><ul><li class=\"dashboard-news-plugin\"><span>Popular Plugin:</span> Clef Two-Factor Authentication&nbsp;<a href=\"plugin-install.php?tab=plugin-information&amp;plugin=wpclef&amp;_wpnonce=0331e3c296&amp;TB_iframe=true&amp;width=600&amp;height=800\" class=\"thickbox open-plugin-details-modal\" aria-label=\"Install Clef Two-Factor Authentication\">(Install)</a></li></ul></div>", "no");
